        height: 65%;
        border: 3px solid $primary_color;
        opacity: 1;
      }
      100% {
        width: 100%;
        height: 100%;
        opacity: 0;
      }
    }
    &.active .line {
      background: transparent;
    }

    &.active .line:before {
      width: 28px;
      top: 0;
      transform: rotate(135deg);
    }

    &.active .line:after {
      width: 28px;
      top: 0;
      transform: rotate(-135deg);
    }

    .line {
      position: absolute;
      top: 50%;
      left: 45%;
      transform: translate(-50%, -50%);
      width: 22px;
      height: 0.25rem;
      background: #fff;
      border-radius: 5px;
      @include transition;

      &:before,
      &:after {
        position: absolute;
        content: "";
        width: 28px;
        height: 0.25rem;
        background: #fff;
        border-radius: 5px;
        @include transition;
      }

      &:before {
        top: -10px;
      }

